import { Link } from "react-router-dom";
import { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import PersonIcon from "@mui/icons-material/Person";
const NavBar = ({ onSearch, cartItemCount }) => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSubmit = () => {
    if (searchQuery.trim().length) {
      onSearch(searchQuery.trim());
    }
    setSearchQuery("");
  };

  return (
    <div className="wrapper">
      <div className="upNav">
        <div className="log_container">
          <img
            src="https://t3.ftcdn.net/jpg/05/64/76/08/360_F_564760847_p8DRmD6MVpCva6vxeLtEBYZAxHWbx5RN.jpg"
            alt="logo"
            className="logo"
          />
        </div>
        <div className="link_container">
          <ul>
            <li>Home</li>
            <li>Shop</li>
            <li>Product</li>
          </ul>
        </div>
        <div className="icon_container">
          <div>
            {" "}
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search products..."
            />
          </div>
          <div>
            <SearchIcon onClick={handleSubmit} />
          </div>
          <div>
            <FavoriteBorderIcon />
          </div>
          <div>
            <PersonIcon />
          </div>
          <div>
            <Link to="/cart" className="link headerCart">
              <img className="cartImg" src="/cart.svg" alt="cart" />
              {cartItemCount > 0 && (
                <div className="cartCounter">
                  {cartItemCount <= 9 ? cartItemCount : "9+"}
                </div>
              )}
            </Link>
          </div>
        </div>
      </div>
      <div className="midNav">
        <img
          src="https://dermacompanies.in/wp-content/uploads/2021/01/Top-Cosmetic-Manufacturers-in-Mumbai.jpg"
          alt=""
          className="image"
        />
      </div>
      <div className="dowNav"></div>
    </div>
  );
};

export { NavBar };
